  proposals (
    first: 20,
    skip: 0,
    where: {
      space_in: ['yam.eth'],
      state: 'closed'
    },
    orderBy: 'created',
    orderDirection: desc
  ) {
    id
    title
    body
    choices
    start
    end
    snapshot
    state
    scores
    scores_by_strategy
    scores_total
    scores_updated
    author
